; mitxela.com/projects/a_tiny_theremin


; PB0 / PB1 are the differential PWM outputs 
  ; I found it sometimes worked better in single-ended mode, 
  ; with the speaker ground connected to the chip ground.
  ; Possibly to do with my speakers' power supply

; PB2 is the detection pulse. 
  ; either connect to mains ground (if the power supply is isolated)
  ; or directly to your body. An ordinary ESD wriststrap probably won't
  ; work because it has a very high resistance in series, for safety.

; PB3 is pitch antenna
; PB4 is volume antenna

.include "tn85def.inc" 
rjmp init

; //////////////// TIMER0_COMPA interrupt /////////////////

.org $0A
	in r0,SREG
	ldi r24,0
	out TCNT0,r24
	
	; r3 is the capacitance reading
	; r4 is a running total to try and average the reading
	add r4,r3
	brcc endint		; wait until r4 overflows
	clr r4

	; advance phase of the sine wave
	; sine table is two dimensional, where ZH = phase, ZL= volume
	
	cpi ZH, HIGH(sineTable*2)+25	; end of table?
	brne dontReset
	ldi ZH, HIGH(sineTable*2)
dontReset:
	lpm r24,Z			; load the value and send it
	out OCR1A,r24		; to the PWM output
	inc ZH

endint:
	out SREG,r0
	reti


;///////////////  init   //////////////

init:
	ldi r16, 0b00000010
	out DDRB,r16


	; PLL output timer1
	ldi r16,(1<<PLLE|1<<PCKE)
	out PLLCSR,r16

	ldi r16, (1<<PWM1A|1<<COM1A0|1<<CS10)
	out TCCR1,r16
	ldi r16,0
	out OCR1A,r16
	ldi r16,255
	out OCR1C,r16
	
	; interrupt setup timer0
	ldi r16,(1<<CS00)
	out TCCR0B,r16
	ldi r16,(1<<OCIE0A)
	out TIMSK,r16
	ldi r16,1
	out OCR0A,r16

	ldi ZH, HIGH(sineTable*2)
	ldi ZL,  LOW(sineTable*2)


;///////////////  main loop  //////////////

main:
	sei
	rcall readCap1	; cap1 is pitch antenna
	mov r3,r16		; store for use in the interrupt

	
	rcall readCap2	; volume antenna
	; For volume we want an absolute number, not to advance a phase.
	; To try and average it a bit, make several readings and add them to r6
	add r6,r16		
	brcc mset1
	clr r6			; if it overflows, 
	dec r6			; clr/dec sets r6 to 255 (saturated)
	rjmp mset2		; and skip ahead

mset1:
	dec r5			; r5 is counting down the number of readings we've made
	brne main		; wait until it hits zero

mset2:
	ldi r16,10		; reset r5 to 10 - number of readings to average
	mov r5,r16
	
	lsr r6			; divide by two and set volume level
	mov ZL,r6		; (LPM takes place in the interrupt)
	clr r6


rjmp main



;///////////////  capacitance readings  ////////////////

.equ tPin=2

readCap1:
	ldi r16,(1<<ADLAR|1<<MUX1) ; Vcc, left adjust, PB4
	out ADMUX,r16

	cbi PORTB,4				; ground both pins
	cbi PORTB,tPin

	ldi r16, 0b00011111		; everything as output
	out DDRB,r16

	rcall wait

	cbi DDRB,4				; set antenna to high-impedance input

	rcall wait
	cli						; disable interrupts (timing critical)
	sbi PORTB,tPin			; set detection pin high

	rcall waitS				; wait a specific amount

	rcall readADC			; take the reading.

	ret


; same again on the other pin
readCap2:
	ldi r16,(1<<ADLAR|1<<MUX1|1<<MUX0) ; Vcc, left adjust, PB3
	out ADMUX,r16

	cbi PORTB,3
	cbi PORTB,tPin

	ldi r16, 0b00011111
	out DDRB,r16

	rcall wait

	cbi DDRB,3

	rcall wait
	cli
	sbi PORTB,tPin

	rcall waitS

	rcall readADC

	ret



readADC:
	; Enable, Start conversion, clear interrupt
	ldi r16,(1<<ADEN|1<<ADSC|1<<ADIF|1<<ADPS2)
	out ADCSRA,r16
	sei
waitForConversion:
	sbis ADCSRA,ADIF
	rjmp waitForConversion
	in r17,ADCL
	in r16,ADCH
	sbrc r17,7	; round up if low byte>=128
	inc r16
	ret




wait:
	ldi r17,10
waitL:
	dec r17
	brne waitL
	ret


; Wait times were determined just by trial and error
waitS:
	ldi r17,8	; This may not be the optimum value
waitSL:
	nop
	nop

	dec r17
	brne waitSL
	ret


